interface II18nConfig {
  languageList?: string[];
  langType: string;
  useLanguage?: string;
  autoWriteAble?: boolean;
  formatSpace?: number;
  sourcePath: string;
  removeUnUseKeys?: boolean;
  patten?: RegExp;
}

interface II18nOptions {
  (opt: II18nConfig): any;
}

export const vitePluginI18n: II18nOptions;
export const serverI18nPlugin: II18nOptions;
export const buildI18nPlugin: II18nOptions;
